import React, { useEffect, useState } from 'react';

const API_URL = '/todos';

const TodoApp = () => {
    const [todos, setTodos] = useState([]);
    const [title, setTitle] = useState('');
    const [loading, setLoading] = useState(false);

    // Fetch todos
    const fetchTodos = async () => {
        setLoading(true);
        const res = await fetch(API_URL);
        const data = await res.json();
        setTodos(data);
        setLoading(false);
    };

    useEffect(() => {
        fetchTodos();
    }, []);

    // Add todo
    const addTodo = async (e) => {
        e.preventDefault();
        if (!title.trim()) return;
        const res = await fetch(API_URL, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ title }),
        });
        if (res.ok) {
            setTitle('');
            fetchTodos();
        }
    };

    // Toggle todo
    const toggleTodo = async (id) => {
        await fetch(`${API_URL}/${id}/toggle`, { method: 'PATCH' });
        fetchTodos();
    };

    // Delete todo
    const deleteTodo = async (id) => {
        await fetch(`${API_URL}/${id}`, { method: 'DELETE' });
        fetchTodos();
    };

    return (
        <div style={{ maxWidth: 400, margin: '2rem auto', padding: 20, border: '1px solid #ccc', borderRadius: 8 }}>
            <h2>Todo App</h2>
            <form onSubmit={addTodo} style={{ marginBottom: 20 }}>
                <input
                    type="text"
                    value={title}
                    onChange={e => setTitle(e.target.value)}
                    placeholder="Add a new todo"
                    style={{ width: '70%', padding: 8 }}
                />
                <button type="submit" style={{ padding: 8, marginLeft: 8 }}>Add</button>
            </form>
            {loading ? (
                <p>Loading...</p>
            ) : (
                <ul style={{ listStyle: 'none', padding: 0 }}>
                    {todos.map(todo => (
                        <li key={todo._id} style={{ marginBottom: 10, display: 'flex', alignItems: 'center' }}>
                            <span
                                onClick={() => toggleTodo(todo._id)}
                                style={{
                                    textDecoration: todo.completed ? 'line-through' : 'none',
                                    cursor: 'pointer',
                                    flex: 1,
                                }}
                            >
                                {todo.title}
                            </span>
                            <button onClick={() => deleteTodo(todo._id)} style={{ marginLeft: 10 }}>Delete</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default TodoApp;
